    gl_FragColor = packRGBE(gl_FragColor.rgb);
    return;
  }
  if (params1.w == 1.) {
    gl_FragColor = texture2D(inputTexture, v_uv);
    gl_FragColor = CCFragOutput(gl_FragColor);
  }
  else if (params2.x == 1.) {
    mediump vec2 v_rgbNW;
    mediump vec2 v_rgbNE;
    mediump vec2 v_rgbSW;
    mediump vec2 v_rgbSE;
    mediump vec2 v_rgbM;
    vec2 resolution = params1.xy;
    vec2 fragCoord = v_uv * resolution;
    fxaaTexcoords(fragCoord, resolution, v_rgbNW, v_rgbNE, v_rgbSW, v_rgbSE, v_rgbM);
    gl_FragColor = fxaa(inputTexture, fragCoord, resolution, v_rgbNW, v_rgbNE, v_rgbSW, v_rgbSE, v_rgbM);
  }
  else {
    gl_FragColor = texture2D(inputTexture, v_uv);
  }
}

